mapper.update:
	    update bxfw_dept set updateTime=now()

	       ,orgCode=#{orgCode}


	       ,deptName=#{deptName}


	       ,note=#{note}

        where deptCode=#{deptCode}

	    update bxfw_position set updateTime=now()

	       ,deptCode=#{deptCode}


	       ,positionName=#{positionName}


	       ,note=#{note}

        where positionCode=#{positionCode}
